              <div style={{ 
                background: 'rgba(255, 255, 255, 0.6)', 
                borderRadius: 12, 
                padding: 24, 
                marginBottom: 32,
                boxShadow: '0 4px 16px rgba(0,0,0,0.1)'
              }}>
                <h3 style={{ color: '#2c3e50', marginBottom: 16 }}>Estad√≠sticas globales</h3>

                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: 16 }}>
                  <div style={{ background: 'rgba(39, 174, 96, 0.1)', padding: 16, borderRadius: 12, textAlign: 'center', border: '2px solid #27ae60' }}>
                    <h4 style={{ margin: 0, color: '#27ae60', fontSize: '1.1em' }}>‚úÖ Aciertos</h4>
                    <p style={{ fontSize: '2em', margin: 0, fontWeight: 'bold', color: '#27ae60' }}>{aciertos}</p>
                  </div>
                  <div style={{ background: 'rgba(231, 76, 60, 0.1)', padding: 16, borderRadius: 12, textAlign: 'center', border: '2px solid #e74c3c' }}>
                    <h4 style={{ margin: 0, color: '#e74c3c', fontSize: '1.1em' }}>‚ùå Fallos</h4>
                    <p style={{ fontSize: '2em', margin: 0, fontWeight: 'bold', color: '#e74c3c' }}>{total - aciertos}</p>
                  </div>
                  <div style={{ background: 'rgba(243, 156, 18, 0.1)', padding: 16, borderRadius: 12, textAlign: 'center', border: '2px solid #f39c12' }}>
                    <h4 style={{ margin: 0, color: '#f39c12', fontSize: '1.1em' }}>üìä Total</h4>
                    <p style={{ fontSize: '2em', margin: 0, fontWeight: 'bold', color: '#f39c12' }}>{total}</p>
                  </div>
                </div>
              </div>

              {/* Estad√≠sticas por tema */}
              <div style={{ 
                background: 'rgba(255, 255, 255, 0.6)', 
                borderRadius: 12, 
                padding: 24,
                boxShadow: '0 4px 16px rgba(0,0,0,0.1)'
              }}>
                <h3 style={{ color: '#2c3e50', marginBottom: 16 }}>Estad√≠sticas por tema</h3>

                {questions.length > 0 ? (
                  <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))', gap: 16 }}>
                    {getTemas(questions).map(tema => {
                      const { total, aciertos, porcentaje } = getEstadisticasUsuario(tema);
                      return (
                        <div key={tema} style={{ 
                          background: 'rgba(52, 152, 219, 0.1)', 
                          padding: 16, 
                          borderRadius: 12,
                          border: '2px solid #3498db',
                          textAlign: 'center'
                        }}>
                          <h4 style={{ margin: 0, color: '#3498db', fontSize: '1.1em' }}>{tema}</h4>
                          <p style={{ margin: '8px 0', fontSize: '1.5em', fontWeight: 'bold', color: '#2c3e50' }}>{aciertos}</p>
                          <p style={{ margin: 0, fontSize: '0.9em', color: '#7f8c8d' }}>Aciertos totales</p>
                        </div>
                      );
                    })}
                  </div>
                ) : (
                  <p style={{ color: '#7f8c8d', textAlign: 'center' }}>No hay suficientes datos para mostrar estad√≠sticas por tema.</p>
                )}
              </div>
            </section>
          )}

          {/* Configuraci√≥n */}
          {pestana === "configuracion" && (
            <section>
              <h2 style={{ color: '#2c3e50', marginBottom: 24 }}>‚öôÔ∏è Configuraci√≥n</h2>

              {/* API Key */}
              <div style={{ 
                background: 'rgba(255, 255, 255, 0.6)', 
                borderRadius: 12, 
                padding: 24, 
                marginBottom: 32,
                boxShadow: '0 4px 16px rgba(0,0,0,0.1)'
              }}>
                <h3 style={{ color: '#2c3e50', marginBottom: 16 }}>Configuraci√≥n de API Key</h3>

                <input
                  type="text"
                  value={groqApiKey}
                  onChange={e => setGroqApiKey(e.target.value)}
                  placeholder="Introduce tu API Key de Groq"
                  style={{
                    width: '100%',
                    padding: 12,
                    borderRadius: 8,
                    border: '1px solid #ddd',
                    fontSize: 16,
                    marginBottom: 16
                  }}
                />
                <button
                  onClick={() => {
                    saveToStorage(STORAGE_KEYS.GROQ_API_KEY, groqApiKey);
                    alert('API Key guardada correctamente.');
                  }}
                  style={{
                    padding: '12px 24px',
                    background: '#27ae60',
                    color: 'white',
                    border: 'none',
                    borderRadius: 8,
                    cursor: 'pointer'
                  }}
                >
                  Guardar API Key
                </button>
              </div>

              {/* Cambio de usuario */}
              <div style={{ 
                background: 'rgba(255, 255, 255, 0.6)', 
                borderRadius: 12, 
                padding: 24,
                boxShadow: '0 4px 16px rgba(0,0,0,0.1)'
              }}>
                <h3 style={{ color: '#2c3e50', marginBottom: 16 }}>Cambio de usuario</h3>

                <input
                  type="text"
                  value={tempUserName}
                  onChange={e => setTempUserName(e.target.value)}
                  placeholder="Introduce el nombre del nuevo usuario"
                  style={{
                    width: '100%',
                    padding: 12,
                    borderRadius: 8,
                    border: '1px solid #ddd',
                    fontSize: 16,
                    marginBottom: 16
                  }}
                />
                <div style={{ display: 'flex', gap: 12 }}>
                  <button
                    onClick={confirmarCambioUsuario}
                    style={{
                      padding: '12px 24px',
                      background: '#3498db',
                      color: 'white',
                      border: 'none',
                      borderRadius: 8,
                      cursor: 'pointer',
                      flex: 1
                    }}
                  >
                    Cambiar usuario
                  </button>
                  <button
                    onClick={cancelarCambioUsuario}
                    style={{
                      padding: '12px 24px',
                      background: '#95a5a6',
                      color: 'white',
                      border: 'none',
                      borderRadius: 8,
                      cursor: 'pointer',
                      flex: 1
                    }}
                  >
                    Cancelar
                  </button>
                </div>

                {/* Lista de usuarios */}
                <div style={{ marginTop: 24 }}>
                  <h4 style={{ color: '#2c3e50', marginBottom: 12 }}>Usuarios existentes</h4>
                  <ul style={{ listStyle: 'none', padding: 0 }}>
                    {Object.keys(STORAGE_KEYS).filter(key => key.startsWith('USER_')).map((key, idx) => {
                      const usuario = key.split('_')[2];
                      const { total, aciertos, porcentaje } = getEstadisticasUsuario(usuario);
                      return (
                        <li key={idx} style={{ 
                          background: 'rgba(52, 152, 219, 0.05)', 
                          padding: 16, 
                          marginBottom: 12, 
                          borderRadius: 8,
                          border: '1px solid rgba(52, 152, 219, 0.2)'
                        }}>
                          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                            <div style={{ flex: 1 }}>
                              <strong style={{ color: '#3498db' }}>{usuario}</strong>
                              <p style={{ margin: '4px 0', fontSize: '0.9em', color: '#7f8c8c' }}>
                                Aciertos: {aciertos} | Total: {total} | {porcentaje}%
                              </p>
                            </div>
                            <button
                              onClick={() => borrarUsuario(usuario)}
                              style={{
                                padding: '8px 12px',
                                background: '#e74c3c',
                                color: 'white',
                                border: 'none',
                                borderRadius: 6,
                                cursor: 'pointer',
                                fontSize: '14px'
                              }}
                            >
                              üóëÔ∏è Borrar
                            </button>
                          </div>
                        </li>
                      );
                    })}
                  </ul>
                </div>
              </div>
            </section>
          )}
        </div>
      </div>
    </div>
  );
}
