import React, { useState } from "react";
import { Question } from "./types";
import AddQuestionForm from "./AddQuestionForm";
import ImportTxtQuestions from "./ImportTxtQuestions";

function shuffle<T>(arr: T[]): T[] {
  return [...arr].sort(() => Math.random() - 0.5);
}

const MODOS = [
  { key: "examen", label: "Simular Examen" },
  { key: "largo", label: "Test Largo (50)" },
  { key: "corto", label: "Test Corto (20)" },
  { key: "repaso", label: "Modo Repaso" },
];

function getTemas(questions: Question[]) {
  const temasUnicos = Array.from(new Set(questions.map(q => q.tema)));
  return ["Todos los temas", ...temasUnicos];
}

export default function App() {
  const [questions, setQuestions] = useState<Question[]>([]);
  const [modo, setModo] = useState<string | null>(null);
  const [testQuestions, setTestQuestions] = useState<Question[]>([]);
  const [current, setCurrent] = useState(0);
  const [temaSeleccionado, setTemaSeleccionado] = useState<string | null>(null);
  const [pendingModo, setPendingModo] = useState<string | null>(null);
  const [answers, setAnswers] = useState<{ [index: number]: string }>({});

  const handleAddQuestion = (q: Question) => setQuestions((prev) => [...prev, q]);
  const handleImportQuestions = (imported: Question[]) => setQuestions((prev) => [...prev, ...imported]);

  const handleAnswer = (letra: string) => {
    setAnswers((prev) => ({ ...prev, [current]: letra }));
  };

  // Nuevo: Selección de tema antes de test largo/corto
  const handleModoClick = (modo: string) => {
    if (modo === "largo" || modo === "corto") {
      setPendingModo(modo);
      setTemaSeleccionado("Todos los temas");
    } else {
      startTest(modo, "Todos los temas");
    }
  };

  const startTest = (modo: string, tema: string) => {
    let selected: Question[] = [];
    let pool = questions;
    if (tema && tema !== "Todos los temas") {
      pool = questions.filter(q => q.tema === tema);
    }
    if (modo === "examen") selected = shuffle(pool);
    if (modo === "largo") selected = shuffle(pool).slice(0, 50);
    if (modo === "corto") selected = shuffle(pool).slice(0, 20);
    if (modo === "repaso") selected = shuffle(pool).slice(0, 10);
    setTestQuestions(selected);
    setCurrent(0);
    setModo(modo);
    setPendingModo(null);
    setTemaSeleccionado(null);
  };

  const endTest = () => {
    setModo(null);
    setTestQuestions([]);
    setCurrent(0);
  };

  // Render selector de tema si corresponde
  if (pendingModo) {
    const temas = getTemas(questions);
    return (
      <div className="App">
        <h2>Selecciona un tema para el test</h2>
        <select value={temaSeleccionado ?? "Todos los temas"} onChange={e => setTemaSeleccionado(e.target.value)}>
          {temas.map(t => (
            <option key={t} value={t}>{t}</option>
          ))}
        </select>
        <div style={{ marginTop: 16 }}>
          <button
            onClick={() => startTest(pendingModo, temaSeleccionado ?? "Todos los temas")}
            disabled={
              !temaSeleccionado ||
              (temaSeleccionado !== "Todos los temas" && questions.filter(q => q.tema === temaSeleccionado).length === 0)
            }
          >
            Empezar test
          </button>
          <button onClick={() => { setPendingModo(null); setTemaSeleccionado(null); }}>Cancelar</button>
        </div>
      </div>
    );
  }

  // Render test mode
  if (modo && testQuestions.length > 0) {
    const q = testQuestions[current];
    const userAnswer = answers[current];
    const isCorrect = userAnswer && userAnswer === q.answer;
    const totalAnswered = Object.keys(answers).length;
    return (
      <div className="App">
        <h2>
          {MODOS.find((m) => m.key === modo)?.label} — Pregunta {current + 1} de {testQuestions.length}
        </h2>
        <div>
          <b>{q.tema}:</b> {q.question}
          <ul>
            {q.options.map((opt, i) => {
              const letra = ["A", "B", "C", "D"][i];
              let color = "";
              if (userAnswer) {
                if (letra === q.answer) color = "#4caf50"; // verde correcta
                if (letra === userAnswer && userAnswer !== q.answer) color = "#e57373"; // rojo fallada
              }
              return (
                <li key={i} style={{ background: color, padding: "0.5em", borderRadius: 6, margin: "0.2em 0" }}>
                  <button
                    disabled={!!userAnswer}
                    style={{
                      background: "none",
                      border: "none",
                      color: "inherit",
                      fontWeight: "bold",
                      cursor: userAnswer ? "default" : "pointer",
                      fontSize: "1em",
                    }}
                    onClick={() => handleAnswer(letra)}
                  >
                    {letra}:
                  </button>{" "}
                  {opt}
                  {userAnswer && letra === q.answer ? " ✅" : ""}
                  {userAnswer && letra === userAnswer && userAnswer !== q.answer ? " ❌" : ""}
                </li>
              );
            })}
          </ul>
        </div>
        {userAnswer && (
          <div style={{ margin: "1em 0", fontWeight: "bold" }}>
            {isCorrect ? "¡Correcto!" : `Incorrecto. Respuesta correcta: ${q.answer}`}
          </div>
        )}
        <div>
          Preguntas respondidas: {totalAnswered} / {testQuestions.length}
        </div>
        <button disabled={current === 0} onClick={() => setCurrent((c) => c - 1)}>
          Anterior
        </button>
        <button
          disabled={current === testQuestions.length - 1}
          onClick={() => setCurrent((c) => c + 1)}
        >
          Siguiente
        </button>
        <button onClick={endTest}>Salir</button>
      </div>
    );
  }

  // Pantalla principal
  return (
    <div className="App">
      <header>
        <h1>App Personal de Tests</h1>
        <p>Selecciona un modo de test o revisa los temas:</p>
      </header>
      <section className="modos">
        {MODOS.map((m) => (
          <button key={m.key} onClick={() => handleModoClick(m.key)} disabled={questions.length === 0}>
            {m.label}
          </button>
        ))}
      </section>
      <ImportTxtQuestions onImport={handleImportQuestions} />
      <AddQuestionForm onAdd={handleAddQuestion} />
      <section>
        <h2>Preguntas añadidas</h2>
        <ul>
          {questions.map((q, idx) => (
            <li key={idx}>
              <b>{q.tema}:</b> {q.question}
              <ul>
                {q.options.map((opt, i) => (
                  <li key={i}>
                    <b>{["A", "B", "C", "D"][i]}:</b> {opt} {q.answer === ["A", "B", "C", "D"][i] ? "✅" : ""}
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}
