import React, { useState, useEffect } from "react";
import { Question } from "./types";
import AddQuestionForm from "./AddQuestionForm";
import ImportTxtQuestions from "./ImportTxtQuestions";
import "./App.css";

function shuffle<T>(arr: T[]): T[] {
  return [...arr].sort(() => Math.random() - 0.5);
}

const MODOS = [
  { key: "examen", label: "Simular Examen" },
  { key: "largo", label: "Test Largo (50)" },
  { key: "corto", label: "Test Corto (20)" },
  { key: "repaso", label: "Modo Repaso" },
];

function getTemas(questions: Question[]) {
  const temasUnicos = Array.from(new Set(questions.map(q => q.tema)));
  return ["Todos los temas", ...temasUnicos];
}

// FunciÃ³n para calcular puntuaciÃ³n: cada 3 fallos resta 1 acierto
function calcularPuntuacion(aciertos: number, fallos: number): number {
  const aciertosEfectivos = aciertos - Math.floor(fallos / 3);
  const total = aciertos + fallos;
  if (total === 0) return 0;
  const puntuacion = (aciertosEfectivos / total) * 10;
  return Math.max(0, Math.round(puntuacion * 100) / 100);
}

// Funciones de persistencia
const STORAGE_KEYS = {
  QUESTIONS: 'personal-testing-app-questions',
  HISTORIAL: 'personal-testing-app-historial',
  DEEPSEEK_API_KEY: 'personal-testing-app-deepseek-key'
};

function saveToStorage(key: string, data: any) {
  try {
    localStorage.setItem(key, JSON.stringify(data));
  } catch (error) {
    console.error('Error guardando en localStorage:', error);
  }
}

function loadFromStorage<T>(key: string, defaultValue: T): T {
  try {
    const stored = localStorage.getItem(key);
    return stored ? JSON.parse(stored) : defaultValue;
  } catch (error) {
    console.error('Error cargando de localStorage:', error);
    return defaultValue;
  }
}

// FunciÃ³n para llamar a DeepSeek
async function getExplanationFromDeepSeek(question: Question, userAnswer: string, apiKey: string): Promise<string> {
  if (!apiKey) {
    return "âš ï¸ No hay API key configurada. Ve a ConfiguraciÃ³n para aÃ±adir tu clave de DeepSeek.";
  }

  try {
    const prompt = `Eres un experto en oposiciones y necesitas explicar esta pregunta de examen:

PREGUNTA: ${question.question}
TEMA: ${question.tema}

OPCIONES:
A) ${question.options[0]}
B) ${question.options[1]}
C) ${question.options[2]}
D) ${question.options[3]}

RESPUESTA CORRECTA: ${question.answer}
RESPUESTA DEL USUARIO: ${userAnswer}
RESULTADO: ${userAnswer === question.answer ? 'CORRECTO' : 'INCORRECTO'}

Por favor, proporciona una explicaciÃ³n clara y educativa de:
1. Por quÃ© la respuesta correcta es la ${question.answer}
2. ${userAnswer !== question.answer ? `Por quÃ© la opciÃ³n ${userAnswer} es incorrecta` : 'Conceptos clave a recordar'}
3. Contexto relevante del tema

Responde en espaÃ±ol, de forma clara y pedagÃ³gica (mÃ¡ximo 300 palabras).`;

    const response = await fetch('https://api.deepseek.com/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${apiKey}`
      },
      body: JSON.stringify({
        model: 'deepseek-chat',
        messages: [
          { role: 'user', content: prompt }
        ],
        max_tokens: 500,
        temperature: 0.7
      })
    });

    if (!response.ok) {
      throw new Error(`Error ${response.status}: ${response.statusText}`);
    }

    const data = await response.json();
    return data.choices[0]?.message?.content || "No se pudo generar una explicaciÃ³n.";

  } catch (error) {
    console.error('Error calling DeepSeek:', error);
    return `âŒ Error al conectar con DeepSeek: ${error instanceof Error ? error.message : 'Error desconocido'}`;
  }
}

