import React, { useState } from "react";
import { Question } from "./types";

const temas = [
  ...Array.from({ length: 10 }, (_, i) => ({ value: `Tema ${i + 1}`, label: `Parte General - Tema ${i + 1}` })),
  ...Array.from({ length: 31 }, (_, i) => ({ value: `Tema ${i + 11}`, label: `Parte Específica - Tema ${i + 11}` })),
];

function ImportTxtQuestions({ onImport }: { onImport: (qs: Question[]) => void }) {
  const [tema, setTema] = useState(temas[0].value);
  const [text, setText] = useState("");
  const [delimiter, setDelimiter] = useState(";");

  const handleImport = () => {
    const lines = text.trim().split("\n").filter(line => line.trim());
    const questions: Question[] = [];
    
    lines.forEach(line => {
      const parts = line.split(delimiter);
      if (parts.length >= 6) {
        questions.push({
          tema: parts[0].trim(),
          question: parts[1].trim(),
          options: [parts[2].trim(), parts[3].trim(), parts[4].trim(), parts[5].trim()],
          answer: parts[6]?.trim() || "A"
        });
      }
    });
    
    if (questions.length > 0) {
      onImport(questions);
      setText("");
    }
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const content = event.target?.result as string;
      const questions = parseQuestions(content);
      if (questions.length > 0) {
        onImport(questions);
        alert(`Se importaron ${questions.length} preguntas correctamente.`);
      } else {
        alert("No se encontraron preguntas válidas en el archivo.");
      }
    };
    reader.readAsText(file, 'utf-8');
  };

  const parseQuestions = (content: string): Question[] => {
    const questions: Question[] = [];
    
    // Dividir por líneas en blanco (doble salto de línea)
    const questionBlocks = content.split(/\n\s*\n/).filter(block => block.trim());
    
    questionBlocks.forEach(block => {
      const lines = block.trim().split('\n').map(line => line.trim());
      
      let questionText = "";
      const options: string[] = [];
      let correctAnswer = "";
      
      lines.forEach(line => {
        if (line.toLowerCase().startsWith('pregunta:')) {
          questionText = line.substring(9).trim(); // Quitar "Pregunta:"
        } else if (line.match(/^[a-d]:/i)) {
          const letter = line.charAt(0).toUpperCase();
          const optionText = line.substring(2).trim();
          
          // Verificar si la respuesta está entre asteriscos
          if (optionText.includes('*')) {
            correctAnswer = letter;
            // Quitar los asteriscos de la opción
            options.push(optionText.replace(/\*/g, ''));
          } else {
            options.push(optionText);
          }
        }
      });
      
      // Validar que tenemos todos los datos necesarios
      if (questionText && options.length === 4 && correctAnswer) {
        questions.push({
          tema,
          question: questionText,
          options,
          answer: correctAnswer
        });
      }
    });
    
    return questions;
  };

  return (
    <div style={{ background: "#fff", padding: 16, borderRadius: 8, marginBottom: 24 }}>
      <h2>Importar preguntas desde archivo TXT</h2>
      
      <div style={{ marginBottom: 16 }}>
        <label>
          Tema para todas las preguntas:
          <select value={tema} onChange={e => setTema(e.target.value)}>
            {temas.map(t => (
              <option key={t.value} value={t.value}>{t.label}</option>
            ))}
          </select>
        </label>
      </div>

      <div style={{ marginBottom: 16 }}>
        <label>
          Seleccionar archivo TXT:
          <input
            type="file"
            accept=".txt"
            onChange={handleFileUpload}
            style={{ marginLeft: 8 }}
          />
        </label>
      </div>

      <div style={{ background: "#f5f5f5", padding: 12, borderRadius: 4, fontSize: "0.9em" }}>
        <h4>Formato esperado:</h4>
        <pre>{`Pregunta: ¿Cuál es la capital de Francia?
a: Madrid
b: *París*
c: Londres
d: Roma

Pregunta: ¿Cuál es la capital de España?
a: *Madrid*
b: Barcelona
c: Valencia
d: Sevilla`}</pre>
        <p><strong>Nota:</strong> La respuesta correcta debe estar entre asteriscos (*)</p>
      </div>
    </div>
  );
}

export default ImportTxtQuestions;
