import React, { useState } from "react";
import { Question } from "./types";
import AddQuestionForm from "./AddQuestionForm";
import ImportTxtQuestions from "./ImportTxtQuestions";
import "./App.css";

const temasGeneral = [
  { id: 1, nombre: "Tema 1", titulo: "El ordenamiento jurídico y las fuentes del Derecho..." },
  { id: 2, nombre: "Tema 2", titulo: "Título III. Las Cortes Generales..." },
  { id: 3, nombre: "Tema 3", titulo: "Título VIII. De la Organización territorial del Estado..." },
  { id: 4, nombre: "Tema 4", titulo: "El Estatuto de Autonomía de Extremadura..." },
  { id: 5, nombre: "Tema 5", titulo: "Las Entidades Locales. El Municipio..." },
  { id: 6, nombre: "Tema 6", titulo: "Ordenanzas y reglamentos de las Entidades Locales..." },
  { id: 7, nombre: "Tema 7", titulo: "El acto administrativo. Clasificación..." },
  { id: 8, nombre: "Tema 8", titulo: "El procedimiento administrativo. Marco jurídico..." },
  { id: 9, nombre: "Tema 9", titulo: "Ley Reguladora de las Bases del Régimen Local..." },
  { id: 10, nombre: "Tema 10", titulo: "La Unión Europea: Referencia histórica..." },
];

const temasEspecifica = [
  { id: 11, nombre: "Tema 11", titulo: "El binomio policía local-ciudadanía..." },
  { id: 12, nombre: "Tema 12", titulo: "Ley Orgánica de Fuerzas y Cuerpos de Seguridad..." },
  { id: 13, nombre: "Tema 13", titulo: "La legislación sobre Policías Locales de la Comunidad Autónoma de Extremadura..." },
  { id: 14, nombre: "Tema 14", titulo: "Normativa de uniformidad, acreditación y equipamiento de Policías Locales de Extremadura..." },
  { id: 15, nombre: "Tema 15", titulo: "Régimen Disciplinario aplicable a policía local de Extremadura..." },
  { id: 16, nombre: "Tema 16", titulo: "Igualdad entre Mujeres y Hombres y prevención de la Violencia de Género en Extremadura..." },
  { id: 17, nombre: "Tema 17", titulo: "Protección Ambiental de la Comunidad Autónoma de Extremadura..." },
  { id: 18, nombre: "Tema 18", titulo: "Ordenación territorial y urbanística sostenible de Extremadura..." },
  { id: 19, nombre: "Tema 19", titulo: "El Código Penal. Título Preliminar..." },
  { id: 20, nombre: "Tema 20", titulo: "El Código Penal. Libro II. Delitos y sus penas..." },
  { id: 21, nombre: "Tema 21", titulo: "El Código Penal. Libro II. Título IX..." },
  { id: 22, nombre: "Tema 22", titulo: "El Código Penal. Libro II. Título XIII..." },
  { id: 23, nombre: "Tema 23", titulo: "El Código Penal. Libro II. Título XV..." },
  { id: 24, nombre: "Tema 24", titulo: "El Código Penal. Libro II. Título XIX..." },
  { id: 25, nombre: "Tema 25", titulo: "La Ley Orgánica de protección de la seguridad ciudadana." },
  { id: 26, nombre: "Tema 26", titulo: "Ley Orgánica reguladora de la responsabilidad penal de los menores..." },
  { id: 27, nombre: "Tema 27", titulo: "La Policía Local como Policía Judicial..." },
  { id: 28, nombre: "Tema 28", titulo: "El Procedimiento de Habeas Corpus..." },
  { id: 29, nombre: "Tema 29", titulo: "Ley sobre Tráfico, Circulación de Vehículos a Motor y Seguridad Vial." },
  { id: 30, nombre: "Tema 30", titulo: "El Reglamento General de Circulación." },
  { id: 31, nombre: "Tema 31", titulo: "El Reglamento General de Vehículos." },
  { id: 32, nombre: "Tema 32", titulo: "El Reglamento General de Conductores." },
  { id: 33, nombre: "Tema 33", titulo: "Inspección Técnica de Vehículos." },
  { id: 34, nombre: "Tema 34", titulo: "El Accidente de Tráfico. Definición, tipos, causas y clases de accidentes..." },
  { id: 35, nombre: "Tema 35", titulo: "Real Decreto regulador de las condiciones de seguridad en el transporte escolar y de menores." },
  { id: 36, nombre: "Tema 36", titulo: "Prevención del consumo de bebidas alcohólicas en la infancia y en la adolescencia." },
  { id: 37, nombre: "Tema 37", titulo: "El Sistema Público de Protección Civil y Emergencias..." },
  { id: 38, nombre: "Tema 38", titulo: "Espectáculos públicos, actividades recreativas y juego en el ámbito de la Comunidad Autónoma de Extremadura..." },
  { id: 39, nombre: "Tema 39", titulo: "Protección de los derechos y el bienestar de los animales..." },
  { id: 40, nombre: "Tema 40", titulo: "Introducción a la Prevención de Riesgos Laborales..." },
  { id: 41, nombre: "Tema 41", titulo: "La protección de datos de carácter personal..." },
];

function TemaSelector({ temas, parte }: { temas: any[]; parte: string }) {
  const [mostrarTitulos, setMostrarTitulos] = useState(false);

  return (
    <div className="temas-section">
      <h2>{parte}</h2>
      <button onClick={() => setMostrarTitulos((v) => !v)}>
        {mostrarTitulos ? "Ocultar títulos" : "Mostrar títulos"}
      </button>
      <ul>
        {temas.map((tema) => (
          <li key={tema.id}>
            <b>{tema.nombre}</b>
            {mostrarTitulos && <span> — {tema.titulo}</span>}
          </li>
        ))}
      </ul>
    </div>
  );
}

function App() {
  const [questions, setQuestions] = useState<Question[]>([]);

  const handleAddQuestion = (q: Question) => {
    setQuestions((prev) => [...prev, q]);
  };

  const handleImportQuestions = (imported: Question[]) => {
    setQuestions((prev) => [...prev, ...imported]);
  };

  return (
    <div className="App">
      <header>
        <h1>App Personal de Tests</h1>
        <p>Selecciona un modo de test o revisa los temas:</p>
      </header>
      <section className="modos">
        <button>Simular Examen</button>
        <button>Test Largo (50)</button>
        <button>Test Corto (20)</button>
        <button>Modo Repaso</button>
      </section>
      <section className="temas">
        <TemaSelector temas={temasGeneral} parte="Parte General" />
        <TemaSelector temas={temasEspecifica} parte="Parte Específica" />
      </section>
      <ImportTxtQuestions onImport={handleImportQuestions} />
      <AddQuestionForm onAdd={handleAddQuestion} />
      <section>
        <h2>Preguntas añadidas</h2>
        <ul>
          {questions.map((q, idx) => (
            <li key={idx}>
              <b>{q.tema}:</b> {q.question}
              <ul>
                {q.options.map((opt, i) => (
                  <li key={i}>
                    <b>{["A", "B", "C", "D"][i]}:</b> {opt} {q.answer === ["A", "B", "C", "D"][i] ? "✅" : ""}
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}

export default App;
