import React, { useState, useEffect } from "react";
import { Question } from "./types";
import AddQuestionForm from "./AddQuestionForm";
import ImportTxtQuestions from "./ImportTxtQuestions";
import "./App.css";

function shuffle<T>(arr: T[]): T[] {
  return [...arr].sort(() => Math.random() - 0.5);
}

const MODOS = [
  { key: "examen", label: "Simular Examen" },
  { key: "largo", label: "Test Largo (50)" },
  { key: "corto", label: "Test Corto (20)" },
  { key: "repaso", label: "Modo Repaso" },
];

function getTemas(questions: Question[]) {
  const temasUnicos = Array.from(new Set(questions.map(q => q.tema)));
  return ["Todos los temas", ...temasUnicos];
}

// Función para calcular puntuación: cada 3 fallos resta 1 acierto
function calcularPuntuacion(aciertos: number, fallos: number): number {
  const aciertosEfectivos = aciertos - Math.floor(fallos / 3);
  const total = aciertos + fallos;
  if (total === 0) return 0;
  const puntuacion = (aciertosEfectivos / total) * 10;
  return Math.max(0, Math.round(puntuacion * 100) / 100);
}

// Funciones de persistencia
const STORAGE_KEYS = {
  QUESTIONS: 'personal-testing-app-questions',
  HISTORIAL: 'personal-testing-app-historial'
};

function saveToStorage(key: string, data: any) {
  try {
    localStorage.setItem(key, JSON.stringify(data));
  } catch (error) {
    console.error('Error guardando en localStorage:', error);
  }
}

function loadFromStorage<T>(key: string, defaultValue: T): T {
  try {
    const stored = localStorage.getItem(key);
    return stored ? JSON.parse(stored) : defaultValue;
  } catch (error) {
    console.error('Error cargando de localStorage:', error);
    return defaultValue;
  }
}

export default function App() {
  // Cargar datos persistentes al inicializar
  const [questions, setQuestions] = useState<Question[]>(() => 
    loadFromStorage(STORAGE_KEYS.QUESTIONS, [])
  );
  const [historial, setHistorial] = useState<Array<{ pregunta: Question, acierto: boolean, fecha: number }>>(() => 
    loadFromStorage(STORAGE_KEYS.HISTORIAL, [])
  );

  const [modo, setModo] = useState<string | null>(null);
  const [testQuestions, setTestQuestions] = useState<Question[]>([]);
  const [current, setCurrent] = useState(0);
  const [temaSeleccionado, setTemaSeleccionado] = useState<string | null>(null);
  const [pendingModo, setPendingModo] = useState<string | null>(null);
  const [answers, setAnswers] = useState<{ [index: number]: string }>({});
  const [pestana, setPestana] = useState<"test" | "preguntas" | "estadisticas">("test");
  const [mostrarAgregar, setMostrarAgregar] = useState(false);
  const [testCompleto, setTestCompleto] = useState(false);

  // Efecto para guardar preguntas cuando cambien
  useEffect(() => {
    saveToStorage(STORAGE_KEYS.QUESTIONS, questions);
  }, [questions]);

  // Efecto para guardar historial cuando cambie
  useEffect(() => {
    saveToStorage(STORAGE_KEYS.HISTORIAL, historial);
  }, [historial]);

  const handleAddQuestion = (q: Question) => setQuestions((prev) => [...prev, q]);

  const handleImportQuestions = (imported: Question[]) => {
    setQuestions((prev) => [...prev, ...imported]);
    // Mostrar mensaje de confirmación
    alert(`Se han añadido ${imported.length} preguntas. Total: ${questions.length + imported.length} preguntas.`);
  };

  const handleAnswer = (letra: string) => {
    setAnswers((prev) => ({ ...prev, [current]: letra }));
    const acierto = letra === testQuestions[current].answer;
    setHistorial((prev) => [
      ...prev,
      { pregunta: testQuestions[current], acierto, fecha: Date.now() },
    ]);
  };

  // Función para limpiar datos (útil para desarrollo/reset)
  const clearAllData = () => {
    if (window.confirm('¿Estás seguro de que quieres borrar TODAS las preguntas e historial? Esta acción no se puede deshacer.')) {
      setQuestions([]);
      setHistorial([]);
      localStorage.removeItem(STORAGE_KEYS.QUESTIONS);
      localStorage.removeItem(STORAGE_KEYS.HISTORIAL);
      alert('Todos los datos han sido borrados.');
    }
  };

  // Función para exportar datos
  const exportData = () => {
    const data = {
      preguntas: questions,
      historial: historial,
      exportDate: new Date().toISOString()
    };
    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `personal-testing-app-backup-${new Date().toISOString().split('T')[0]}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  // Estadísticas
  const total = historial.length;
  const aciertos = historial.filter(h => h.acierto).length;
  const porcentajeGlobal = total ? Math.round((aciertos / total) * 100) : 0;
  function porcentajeTema(tema: string) {
    const ultimas = historial.filter(h => h.pregunta.tema === tema).slice(-100);
    const aciertosTema = ultimas.filter(h => h.acierto).length;
    return ultimas.length ? Math.round((aciertosTema / ultimas.length) * 100) : 0;
  }

  // Modo repaso inteligente
  const startTest = (modo: string, tema: string) => {
    let pool = questions;
    if (tema && tema !== "Todos los temas") {
      pool = questions.filter(q => q.tema === tema);
    }
    let selected: Question[] = [];
    if (modo === "repaso") {
      const falloCount: { [key: string]: number } = {};
      historial.forEach(h => {
        const key = h.pregunta.question + "|" + h.pregunta.tema;
        if (!h.acierto) falloCount[key] = (falloCount[key] || 0) + 1;
      });
      selected = [...pool]
        .sort((a, b) => {
          const ka = a.question + "|" + a.tema;
          const kb = b.question + "|" + b.tema;
          return (falloCount[kb] || 0) - (falloCount[ka] || 0);
        })
        .slice(0, 20);
    } else if (modo === "examen") selected = shuffle(pool).slice(0, 100);
    else if (modo === "largo") selected = shuffle(pool).slice(0, 50);
    else if (modo === "corto") selected = shuffle(pool).slice(0, 20);
    setTestQuestions(selected);
    setCurrent(0);
    setModo(modo);
    setPendingModo(null);
    setTemaSeleccionado(null);
    setAnswers({});
    setTestCompleto(false);
  };

  const handleModoClick = (modo: string) => {
    if (modo === "largo" || modo === "corto") {
      setPendingModo(modo);
      setTemaSeleccionado("Todos los temas");
    } else {
      startTest(modo, "Todos los temas");
    }
  };

  const endTest = () => {
    setModo(null);
    setTestQuestions([]);
    setCurrent(0);
    setAnswers({});
    setTestCompleto(false);
  };

  const finalizarTest = () => {
    setTestCompleto(true);
  };

  // Selector de tema para test largo/corto
  if (pendingModo) {
    const temas = getTemas(questions);
    return (
      <div className="App">
        <h2>Selecciona un tema para el test</h2>
        <select value={temaSeleccionado ?? "Todos los temas"} onChange={e => setTemaSeleccionado(e.target.value)}>
          {temas.map(t => (
            <option key={t} value={t}>{t}</option>
          ))}
        </select>
        <div style={{ marginTop: 16 }}>
          <button
            onClick={() => startTest(pendingModo, temaSeleccionado ?? "Todos los temas")}
            disabled={
              !temaSeleccionado ||
              (temaSeleccionado !== "Todos los temas" && questions.filter(q => q.tema === temaSeleccionado).length === 0)
            }
          >
            Empezar test
          </button>
          <button onClick={() => { setPendingModo(null); setTemaSeleccionado(null); }}>Cancelar</button>
        </div>
      </div>
    );
  }

  // Pantalla de resultados
  if (testCompleto && testQuestions.length > 0) {
    const respondidas = Object.keys(answers).length;
    const respuestasTest = testQuestions.map((q, i) => ({
      pregunta: q,
      respuesta: answers[i] || null,
      correcta: answers[i] === q.answer
    }));

    const aciertosTest = respuestasTest.filter(r => r.correcta).length;
    const fallosTest = respuestasTest.filter(r => r.respuesta && !r.correcta).length;
    const puntuacion = calcularPuntuacion(aciertosTest, fallosTest);

    return (
      <div className="App">
        <h2>🎯 Resultados del {MODOS.find(m => m.key === modo)?.label}</h2>

        <div style={{ 
          background: '#f8f9fa', 
          padding: 24, 
          borderRadius: 8, 
          marginBottom: 24,
          textAlign: 'center'
        }}>
          <h3 style={{ fontSize: '2em', margin: 0, color: puntuacion >= 5 ? '#4caf50' : '#f44336' }}>
            {puntuacion}/10
          </h3>
          <p style={{ margin: '8px 0', fontSize: '1.2em' }}>
            {puntuacion >= 5 ? '🎉 ¡Aprobado!' : '❌ Suspenso'}
          </p>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: 16, marginBottom: 24 }}>
          <div style={{ background: '#e8f5e8', padding: 16, borderRadius: 6, textAlign: 'center' }}>
            <h4 style={{ margin: 0, color: '#2e7d32' }}>✅ Aciertos</h4>
            <p style={{ fontSize: '1.5em', margin: '8px 0' }}>{aciertosTest}</p>
          </div>
          <div style={{ background: '#ffebee', padding: 16, borderRadius: 6, textAlign: 'center' }}>
            <h4 style={{ margin: 0, color: '#c62828' }}>❌ Fallos</h4>
            <p style={{ fontSize: '1.5em', margin: '8px 0' }}>{fallosTest}</p>
          </div>
          <div style={{ background: '#fff3e0', padding: 16, borderRadius: 6, textAlign: 'center' }}>
            <h4 style={{ margin: 0, color: '#ef6c00' }}>⏭️ Sin responder</h4>
            <p style={{ fontSize: '1.5em', margin: '8px 0' }}>{testQuestions.length - respondidas}</p>
          </div>
          <div style={{ background: '#e3f2fd', padding: 16, borderRadius: 6, textAlign: 'center' }}>
            <h4 style={{ margin: 0, color: '#1565c0' }}>📊 Total</h4>
            <p style={{ fontSize: '1.5em', margin: '8px 0' }}>{testQuestions.length}</p>
          </div>
        </div>

        <div style={{ background: '#fff', padding: 16, borderRadius: 8, marginBottom: 24 }}>
          <h4>💡 Cálculo de puntuación:</h4>
          <p>• Aciertos efectivos: {aciertosTest} - {Math.floor(fallosTest / 3)} = {aciertosTest - Math.floor(fallosTest / 3)}</p>
          <p>• Cada 3 fallos resta 1 acierto</p>
          <p>• Puntuación: ({aciertosTest - Math.floor(fallosTest / 3)} / {respondidas}) × 10 = {puntuacion}</p>
        </div>

        <div style={{ display: 'flex', gap: 12, justifyContent: 'center' }}>
          <button 
            onClick={endTest}
            style={{ 
              padding: '12px 24px', 
              background: '#2196f3', 
              color: 'white', 
              border: 'none', 
              borderRadius: 6 
            }}
          >
            Volver al menú
          </button>
          <button 
            onClick={() => setTestCompleto(false)}
            style={{ 
              padding: '12px 24px', 
              background: '#4caf50', 
              color: 'white', 
              border: 'none', 
              borderRadius: 6 
            }}
          >
            Revisar respuestas
          </button>
        </div>
      </div>
    );
  }

  // Render test mode
  if (modo && testQuestions.length > 0) {
    const q = testQuestions[current];
    const userAnswer = answers[current];
    const isCorrect = userAnswer && userAnswer === q.answer;
    const totalAnswered = Object.keys(answers).length;

    // Calcular puntuación en tiempo real
    const respuestasHastaAhora = Object.entries(answers).map(([index, respuesta]) => ({
      correcta: respuesta === testQuestions[parseInt(index)].answer
    }));
    const aciertosHastaAhora = respuestasHastaAhora.filter(r => r.correcta).length;
    const fallosHastaAhora = respuestasHastaAhora.filter(r => !r.correcta).length;
    const puntuacionActual = totalAnswered > 0 ? calcularPuntuacion(aciertosHastaAhora, fallosHastaAhora) : 0;

    return (
      <div className="App">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 16 }}>
          <h2>
            {MODOS.find((m) => m.key === modo)?.label} — Pregunta {current + 1} de {testQuestions.length}
          </h2>
          <div style={{ background: '#f0f0f0', padding: '8px 16px', borderRadius: 6 }}>
            <strong>Puntuación actual: {puntuacionActual}/10</strong>
          </div>
        </div>

        <div>
          <b>{q.tema}:</b> {q.question}
          <ul>
            {q.options.map((opt, i) => {
              const letra = ["A", "B", "C", "D"][i];
              let color = "";
              if (userAnswer) {
                if (letra === q.answer) color = "#4caf50";
                if (letra === userAnswer && userAnswer !== q.answer) color = "#e57373";
              }
              return (
                <li key={i} style={{ background: color, padding: "0.5em", borderRadius: 6, margin: "0.2em 0" }}>
                  <button
                    disabled={!!userAnswer}
                    style={{
                      background: "none",
                      border: "none",
                      color: "inherit",
                      fontWeight: "bold",
                      cursor: userAnswer ? "default" : "pointer",
                      fontSize: "1em",
                    }}
                    onClick={() => handleAnswer(letra)}
                  >
                    {letra}:
                  </button>{" "}
                  {opt}
                  {userAnswer && letra === q.answer ? " ✅" : ""}
                  {userAnswer && letra === userAnswer && userAnswer !== q.answer ? " ❌" : ""}
                </li>
              );
            })}
          </ul>
        </div>
        {userAnswer && (
          <div style={{ margin: "1em 0", fontWeight: "bold" }}>
            {isCorrect ? "¡Correcto!" : `Incorrecto. Respuesta correcta: ${q.answer}`}
          </div>
        )}
        <div style={{ marginBottom: 16 }}>
          Preguntas respondidas: {totalAnswered} / {testQuestions.length} | 
          ✅ {aciertosHastaAhora} | ❌ {fallosHastaAhora}
        </div>
        <div style={{ display: 'flex', gap: 8 }}>
          <button disabled={current === 0} onClick={() => setCurrent((c) => c - 1)}>
            Anterior
          </button>
          <button
            disabled={current === testQuestions.length - 1}
            onClick={() => setCurrent((c) => c + 1)}
          >
            Siguiente
          </button>
          <button 
            onClick={finalizarTest}
            style={{ 
              background: '#4caf50', 
              color: 'white', 
              border: 'none', 
              padding: '8px 16px', 
              borderRadius: 4 
            }}
          >
            Finalizar test
          </button>
          <button onClick={endTest}>Salir</button>
        </div>
      </div>
    );
  }

  // Pantalla principal con pestañas
  return (
    <div className="App">
      <div style={{ marginBottom: 24 }}>
        <button onClick={() => setPestana("test")}>Modo Test</button>
        <button onClick={() => setPestana("preguntas")}>Preguntas añadidas</button>
        <button onClick={() => setPestana("estadisticas")}>Estadísticas</button>
      </div>

      {pestana === "test" && (
        <>
          <button onClick={() => setMostrarAgregar((v) => !v)}>
            {mostrarAgregar ? "Cerrar menú de añadir preguntas" : "Añadir preguntas"}
          </button>
          {mostrarAgregar && (
            <>
              <ImportTxtQuestions onImport={handleImportQuestions} />
              <AddQuestionForm onAdd={handleAddQuestion} />
            </>
          )}

          {questions.length > 0 && (
            <div style={{ marginTop: 24 }}>
              <h3>Iniciar Test ({questions.length} preguntas disponibles)</h3>
              <div style={{ display: 'flex', gap: 12, flexWrap: 'wrap' }}>
                {MODOS.map(modo => (
                  <button
                    key={modo.key}
                    onClick={() => handleModoClick(modo.key)}
                    style={{
                      padding: '12px 24px',
                      background: '#2196f3',
                      color: 'white',
                      border: 'none',
                      borderRadius: 6,
                      cursor: 'pointer',
                      fontSize: '1em'
                    }}
                  >
                    {modo.label}
                  </button>
                ))}
              </div>

              <div style={{ marginTop: 16 }}>
                <h4>Descripción de los modos:</h4>
                <ul style={{ textAlign: 'left' }}>
                  <li><strong>Simular Examen:</strong> 100 preguntas aleatorias</li>
                  <li><strong>Test Largo:</strong> 50 preguntas aleatorias</li>
                  <li><strong>Test Corto:</strong> 20 preguntas aleatorias</li>
                  <li><strong>Modo Repaso:</strong> 20 preguntas priorizando las que más fallas</li>
                </ul>
                <p style={{ background: '#e3f2fd', padding: 12, borderRadius: 6, marginTop: 16 }}>
                  <strong>📊 Sistema de puntuación:</strong> Puntuación sobre 10. Cada 3 respuestas incorrectas resta 1 acierto.
                </p>
              </div>
            </div>
          )}

          {questions.length === 0 && (
            <div style={{ marginTop: 24, padding: 16, background: '#fff3cd', borderRadius: 6 }}>
              <p>No hay preguntas cargadas. Añade preguntas para poder iniciar un test.</p>
            </div>
          )}
        </>
      )}

      {pestana === "preguntas" && (
        <section>
          <h2>Preguntas añadidas ({questions.length})</h2>

          {/* Botones de gestión */}
          <div style={{ marginBottom: 16, display: 'flex', gap: 8, flexWrap: 'wrap' }}>
            <button 
              onClick={exportData}
              style={{ 
                background: '#4caf50', 
                color: 'white', 
                border: 'none', 
                padding: '8px 16px', 
                borderRadius: 4 
              }}
            >
              📥 Exportar backup
            </button>
            <button 
              onClick={clearAllData}
              style={{ 
                background: '#f44336', 
                color: 'white', 
                border: 'none', 
                padding: '8px 16px', 
                borderRadius: 4 
              }}
            >
              🗑️ Borrar todo
            </button>
          </div>

          <ul>
            {questions.map((q, idx) => (
              <li key={idx}>
                <b>{q.tema}:</b> {q.question}
              </li>
            ))}
          </ul>
        </section>
      )}

      {pestana === "estadisticas" && (
        <section>
          <h2>Estadísticas</h2>
          <div>Total preguntas: {questions.length}</div>
          <div>Total respuestas: {historial.length}</div>
          <div>Aciertos: {aciertos} ({porcentajeGlobal}%)</div>
          <div style={{ marginTop: 16 }}>
            <small>Los datos se guardan automáticamente en tu navegador.</small>
          </div>
        </section>
      )}
    </div>
  );
}
