import React, { useState, useEffect } from "react";
import { Question } from "./types";
import AddQuestionForm from "./AddQuestionForm";
import ImportTxtQuestions from "./ImportTxtQuestions";
import "./App.css";

function shuffle<T>(arr: T[]): T[] {
  return [...arr].sort(() => Math.random() - 0.5);
}

const MODOS = [
  { key: "examen", label: "Simular Examen" },
  { key: "largo", label: "Test Largo (50)" },
  { key: "corto", label: "Test Corto (20)" },
  { key: "repaso", label: "Modo Repaso" },
];

function getTemas(questions: Question[]) {
  const temasUnicos = Array.from(new Set(questions.map(q => q.tema)));
  return ["Todos los temas", ...temasUnicos];
}

// FunciÃ³n para calcular puntuaciÃ³n: cada 3 fallos resta 1 acierto
function calcularPuntuacion(aciertos: number, fallos: number): number {
  const aciertosEfectivos = aciertos - Math.floor(fallos / 3);
  const total = aciertos + fallos;
  if (total === 0) return 0;
  const puntuacion = (aciertosEfectivos / total) * 10;
  return Math.max(0, Math.round(puntuacion * 100) / 100);
}

// Funciones de persistencia
const STORAGE_KEYS = {
  QUESTIONS: 'personal-testing-app-questions',
  HISTORIAL: 'personal-testing-app-historial',
  GROQ_API_KEY: 'personal-testing-app-groq-key'
};

function saveToStorage(key: string, data: any) {
  try {
    localStorage.setItem(key, JSON.stringify(data));
  } catch (error) {
    console.error('Error guardando en localStorage:', error);
  }
}

function loadFromStorage<T>(key: string, defaultValue: T): T {
  try {
    const stored = localStorage.getItem(key);
    return stored ? JSON.parse(stored) : defaultValue;
  } catch (error) {
    console.error('Error cargando de localStorage:', error);
    return defaultValue;
  }
}

// FunciÃ³n mejorada para llamar a Groq con prompt optimizado - VERSIÃ“N CONCISA
async function getExplanationFromGroq(question: Question, userAnswer: string, apiKey: string): Promise<string> {
  if (!apiKey) {
    return "âš ï¸ No hay API key configurada. Ve a ConfiguraciÃ³n para aÃ±adir tu clave de Groq.";
  }

  try {
    const prompt = `Eres un experto preparador de oposiciones. Proporciona una explicaciÃ³n BREVE y CONCISA:

PREGUNTA: ${question.question}
TEMA: ${question.tema}

OPCIONES:
A) ${question.options[0]}
B) ${question.options[1]}
C) ${question.options[2]}
D) ${question.options[3]}

RESPUESTA CORRECTA: ${question.answer}
TU RESPUESTA: ${userAnswer}

Explica en MÃXIMO 80 palabras:
1. Base legal (artÃ­culo/ley especÃ­fica)
2. Por quÃ© ${question.answer} es correcta
3. ${userAnswer !== question.answer ? `Por quÃ© ${userAnswer} es incorrecta` : 'Concepto clave'}

Formato: "ðŸ“š [Base legal] - [ExplicaciÃ³n breve]"
Ejemplo: "ðŸ“š Art. 103 CE - La AdministraciÃ³n sirve con objetividad los intereses generales..."

Respuesta CONCISA:`;

    const response = await fetch('https://api.groq.com/openai/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${apiKey}`,
        'User-Agent': 'Personal-Testing-App/1.0'
      },
      body: JSON.stringify({
        model: 'llama3-8b-8192',
        messages: [
          {
            role: 'system',
            content: 'Eres un preparador de oposiciones espaÃ±ol que da explicaciones ULTRA-CONCISAS. MÃ¡ximo 80 palabras por respuesta. Siempre incluye la base legal especÃ­fica (artÃ­culo, ley) al inicio. Formato: "ðŸ“š [Base legal] - [ExplicaciÃ³n breve]". Sin pÃ¡rrafos largos, directo al grano.'
          },
          {
            role: 'user',
            content: prompt
          }
        ],
        max_tokens: 150,
        temperature: 0.1,
        stream: false
      })
    });

    if (!response.ok) {
      const errorText = await response.text();
      console.error('Groq API Error:', response.status, errorText);
      throw new Error(`Error ${response.status}: ${errorText}`);
    }

    const data = await response.json();

    if (!data.choices || !data.choices[0] || !data.choices[0].message) {
      console.error('Unexpected Groq response:', data);
      return "âŒ Respuesta inesperada de Groq.";
    }

    return data.choices[0].message.content || "No se pudo generar una explicaciÃ³n.";

  } catch (error) {
    console.error('Error calling Groq:', error);

    if (error instanceof Error) {
      if (error.message.includes('401')) {
        return "âŒ API key invÃ¡lida. Verifica tu clave en ConfiguraciÃ³n.";
      } else if (error.message.includes('429')) {
        return "â° LÃ­mite de peticiones excedido. Espera un momento e intÃ©ntalo de nuevo.";
      } else if (error.message.includes('400')) {
        return "âŒ Error en el formato de la peticiÃ³n.";
      }
      return `âŒ Error: ${error.message}`;
    }

    return "âŒ Error desconocido al conectar con Groq.";
  }
}

export default function App() {
  // Cargar datos persistentes al inicializar
  const [questions, setQuestions] = useState<Question[]>(() => 
    loadFromStorage(STORAGE_KEYS.QUESTIONS, [])
  );
  const [historial, setHistorial] = useState<Array<{ pregunta: Question, acierto: boolean, fecha: number }>>(() => 
    loadFromStorage(STORAGE_KEYS.HISTORIAL, [])
  );
  const [groqApiKey, setGroqApiKey] = useState<string>(() => 
    loadFromStorage(STORAGE_KEYS.GROQ_API_KEY, "")
  );

  const [modo, setModo] = useState<string | null>(null);
  const [testQuestions, setTestQuestions] = useState<Question[]>([]);
  const [current, setCurrent] = useState(0);
  const [temaSeleccionado, setTemaSeleccionado] = useState<string | null>(null);
  const [pendingModo, setPendingModo] = useState<string | null>(null);
  const [answers, setAnswers] = useState<{ [index: number]: string }>({});
  const [pestana, setPestana] = useState<"test" | "preguntas" | "estadisticas" | "configuracion">("test");
  const [testCompleto, setTestCompleto] = useState(false);

  // Estados para explicaciones Groq
  const [explanations, setExplanations] = useState<{ [index: number]: string }>({});
  const [loadingExplanation, setLoadingExplanation] = useState<{ [index: number]: boolean }>({});

  // Efectos para guardar datos
  useEffect(() => {
    saveToStorage(STORAGE_KEYS.QUESTIONS, questions);
  }, [questions]);

  useEffect(() => {
    saveToStorage(STORAGE_KEYS.HISTORIAL, historial);
  }, [historial]);

  useEffect(() => {
    saveToStorage(STORAGE_KEYS.GROQ_API_KEY, groqApiKey);
  }, [groqApiKey]);

  const handleAddQuestion = (q: Question) => setQuestions((prev) => [...prev, q]);

  const handleImportQuestions = (imported: Question[]) => {
    setQuestions((prev) => [...prev, ...imported]);
    alert(`Se han aÃ±adido ${imported.length} preguntas. Total: ${questions.length + imported.length} preguntas.`);
  };

  const handleAnswer = (letra: string) => {
    setAnswers((prev) => ({ ...prev, [current]: letra }));
    const acierto = letra === testQuestions[current].answer;
    setHistorial((prev) => [
      ...prev,
      { pregunta: testQuestions[current], acierto, fecha: Date.now() },
    ]);

    // Auto-explicaciÃ³n si la respuesta es incorrecta
    if (!acierto && groqApiKey) {
      getExplanation(current, letra);
    }
  };

  // FunciÃ³n para obtener explicaciÃ³n
  const getExplanation = async (questionIndex: number, userAnswer: string) => {
    if (!groqApiKey) {
      alert("Configura tu API key de Groq en ConfiguraciÃ³n para obtener explicaciones.");
      return;
    }

    setLoadingExplanation(prev => ({ ...prev, [questionIndex]: true }));

    try {
      const explanation = await getExplanationFromGroq(
        testQuestions[questionIndex], 
        userAnswer, 
        groqApiKey
      );
      setExplanations(prev => ({ ...prev, [questionIndex]: explanation }));
    } finally {
      setLoadingExplanation(prev => ({ ...prev, [questionIndex]: false }));
    }
  };

  // FunciÃ³n para recuperar preguntas del localStorage
  const recoverQuestionsFromStorage = () => {
    try {
      const stored = localStorage.getItem(STORAGE_KEYS.QUESTIONS);
      if (stored) {
        const storedQuestions: Question[] = JSON.parse(stored);
        if (storedQuestions.length > 0) {
          const currentKeys = new Set(questions.map(q => `${q.question}|${q.tema}`));
          const newQuestions = storedQuestions.filter(q => !currentKeys.has(`${q.question}|${q.tema}`));

          if (newQuestions.length > 0) {
            setQuestions((prev) => [...prev, ...newQuestions]);
            alert(`âœ… Recuperadas ${newQuestions.length} preguntas del navegador. Total: ${questions.length + newQuestions.length} preguntas.`);
          } else {
            alert('â„¹ï¸ No se encontraron preguntas nuevas en el navegador (todas ya estÃ¡n cargadas).');
          }
        } else {
          alert('â„¹ï¸ No hay preguntas guardadas en el navegador.');
        }
      } else {
        alert('â„¹ï¸ No se encontraron preguntas guardadas en el navegador.');
      }
    } catch (error) {
      console.error('Error recuperando preguntas:', error);
      alert('âŒ Error al recuperar preguntas del navegador.');
    }
  };

  // FunciÃ³n para mostrar info de localStorage
  const showStorageInfo = () => {
    try {
      const stored = localStorage.getItem(STORAGE_KEYS.QUESTIONS);
      const historialStored = localStorage.getItem(STORAGE_KEYS.HISTORIAL);

      let message = 'ðŸ“Š InformaciÃ³n del navegador:\n\n';

      if (stored) {
        const storedQuestions: Question[] = JSON.parse(stored);
        message += `ðŸ”¹ Preguntas guardadas: ${storedQuestions.length}\n`;
        message += `ðŸ”¹ Preguntas cargadas actualmente: ${questions.length}\n\n`;
      } else {
        message += `ðŸ”¹ No hay preguntas guardadas\n\n`;
      }

      if (historialStored) {
        const storedHistorial = JSON.parse(historialStored);
        message += `ðŸ“ˆ Respuestas en historial: ${storedHistorial.length}\n`;
      } else {
        message += `ðŸ“ˆ No hay historial guardado\n`;
      }

      message += `\nðŸ’¾ Los datos se guardan automÃ¡ticamente en tu navegador.`;
      alert(message);
    } catch (error) {
      alert('âŒ Error al leer informaciÃ³n del navegador.');
    }
  };

  // FunciÃ³n para limpiar datos
  const clearAllData = () => {
    if (window.confirm('Â¿EstÃ¡s seguro de que quieres borrar TODAS las preguntas e historial? Esta acciÃ³n no se puede deshacer.')) {
      setQuestions([]);
      setHistorial([]);
      localStorage.removeItem(STORAGE_KEYS.QUESTIONS);
      localStorage.removeItem(STORAGE_KEYS.HISTORIAL);
      alert('Todos los datos han sido borrados.');
    }
  };

  // FunciÃ³n para exportar datos
  const exportData = () => {
    const data = {
      preguntas: questions,
      historial: historial,
      exportDate: new Date().toISOString()
    };
    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `personal-testing-app-backup-${new Date().toISOString().split('T')[0]}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  // EstadÃ­sticas
  const total = historial.length;
  const aciertos = historial.filter(h => h.acierto).length;
  const porcentajeGlobal = total ? Math.round((aciertos / total) * 100) : 0;
  function porcentajeTema(tema: string) {
    const ultimas = historial.filter(h => h.pregunta.tema === tema).slice(-100);
    const aciertosTema = ultimas.filter(h => h.acierto).length;
    return ultimas.length ? Math.round((aciertosTema / ultimas.length) * 100) : 0;
  }

  // Modo repaso inteligente
  const startTest = (modo: string, tema: string) => {
    let pool = questions;
    if (tema && tema !== "Todos los temas") {
      pool = questions.filter(q => q.tema === tema);
    }
    let selected: Question[] = [];
    if (modo === "repaso") {
      const falloCount: { [key: string]: number } = {};
      historial.forEach(h => {
        const key = h.pregunta.question + "|" + h.pregunta.tema;
        if (!h.acierto) falloCount[key] = (falloCount[key] || 0) + 1;
      });
      selected = [...pool]
        .sort((a, b) => {
          const ka = a.question + "|" + a.tema;
          const kb = b.question + "|" + b.tema;
          return (falloCount[kb] || 0) - (falloCount[ka] || 0);
        })
        .slice(0, 20);
    } else if (modo === "examen") selected = shuffle(pool).slice(0, 100);
    else if (modo === "largo") selected = shuffle(pool).slice(0, 50);
    else if (modo === "corto") selected = shuffle(pool).slice(0, 20);
    setTestQuestions(selected);
    setCurrent(0);
    setModo(modo);
    setPendingModo(null);
    setTemaSeleccionado(null);
    setAnswers({});
    setTestCompleto(false);
    setExplanations({});
    setLoadingExplanation({});
  };

  const handleModoClick = (modo: string) => {
    if (modo === "largo" || modo === "corto") {
      setPendingModo(modo);
      setTemaSeleccionado("Todos los temas");
    } else {
      startTest(modo, "Todos los temas");
    }
  };

  const endTest = () => {
    setModo(null);
    setTestQuestions([]);
    setCurrent(0);
    setAnswers({});
    setTestCompleto(false);
    setExplanations({});
    setLoadingExplanation({});
  };

  const finalizarTest = () => {
    setTestCompleto(true);
  };

  // Selector de tema para test largo/corto
  if (pendingModo) {
    const temas = getTemas(questions);
    return (
      <div style={{
        minHeight: '100vh',
        backgroundImage: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        display: 'flex'
      }}>
        {/* Sidebar Navigation */}
        <div style={{
          width: 280,
          background: 'rgba(255, 255, 255, 0.95)',
          padding: 24,
          boxShadow: '2px 0 10px rgba(0,0,0,0.1)'
        }}>
          <h2 style={{ color: '#2c3e50', marginBottom: 32, fontSize: '1.5em', fontWeight: 'bold' }}>Personal Testing</h2>
          <nav>
            {[
              { key: "test", label: "ðŸŽ¯ Modo Test", active: false },
              { key: "preguntas", label: "ðŸ“š Preguntas", active: false },
              { key: "estadisticas", label: "ðŸ“Š EstadÃ­sticas", active: false },
              { key: "configuracion", label: "âš™ï¸ ConfiguraciÃ³n", active: false }
            ].map(item => (
              <button
                key={item.key}
                onClick={() => {
                  setPendingModo(null);
                  setTemaSeleccionado(null);
                  setPestana(item.key as any);
                }}
                style={{
                  display: 'block',
                  width: '100%',
                  padding: '12px 16px',
                  margin: '8px 0',
                  background: item.active ? '#4a90e2' : 'transparent',
                  color: item.active ? 'white' : '#5a6c7d',
                  border: 'none',
                  borderRadius: 8,
                  textAlign: 'left',
                  cursor: 'pointer',
                  fontSize: '16px',
                  fontWeight: item.active ? '600' : '500',
                  transition: 'all 0.2s ease'
                }}
              >
                {item.label}
              </button>
            ))}
          </nav>
        </div>

        {/* Main Content */}
        <div style={{
          flex: 1,
          padding: 40,
          background: 'rgba(255, 255, 255, 0.5)',
          minHeight: '100vh'
        }}>
          <div style={{
            background: 'rgba(255, 255, 255, 0.9)',
            borderRadius: 16,
            padding: 32,
            boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
            minHeight: 'calc(100vh - 80px)'
          }}>
            <h2 style={{ color: '#2c3e50', marginBottom: 24 }}>Selecciona un tema para el test</h2>
            <select 
              value={temaSeleccionado ?? "Todos los temas"} 
              onChange={e => setTemaSeleccionado(e.target.value)}
              style={{
                width: '100%',
                padding: '12px 16px',
                borderRadius: 8,
                border: '2px solid #e0e6ed',
                fontSize: '16px',
                marginBottom: 24
              }}
            >
              {temas.map(t => (
                <option key={t} value={t}>{t}</option>
              ))}
            </select>
            <div style={{ display: 'flex', gap: 12 }}>
              <button
                onClick={() => startTest(pendingModo, temaSeleccionado ?? "Todos los temas")}
                disabled={
                  !temaSeleccionado ||
                  (temaSeleccionado !== "Todos los temas" && questions.filter(q => q.tema === temaSeleccionado).length === 0)
                }
                style={{
                  padding: '12px 24px',
                  background: '#4CAF50',
                  color: 'white',
                  border: 'none',
                  borderRadius: 8,
                  cursor: 'pointer',
                  fontSize: '16px',
                  fontWeight: '600'
                }}
              >
                Empezar test
              </button>
              <button 
                onClick={() => { setPendingModo(null); setTemaSeleccionado(null); }}
                style={{
                  padding: '12px 24px',
                  background: '#f8f9fa',
                  color: '#5a6c7d',
                  border: '2px solid #e0e6ed',
                  borderRadius: 8,
                  cursor: 'pointer',
                  fontSize: '16px',
                  fontWeight: '600'
                }}
              >
                Cancelar
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

