import React, { useState } from "react";
import { Question } from "./types";

const temas = [
  ...Array.from({ length: 10 }, (_, i) => ({ value: `Tema ${i + 1}`, label: `Parte General - Tema ${i + 1}` })),
  ...Array.from({ length: 31 }, (_, i) => ({ value: `Tema ${i + 11}`, label: `Parte Específica - Tema ${i + 11}` })),
];

type Props = {
  onImport: (questions: Question[]) => void;
};

export default function ImportTxtQuestions({ onImport }: Props) {
  const [pendingQuestions, setPendingQuestions] = useState<Omit<Question, "tema">[]>([]);
  const [current, setCurrent] = useState(0);
  const [selectedTema, setSelectedTema] = useState(temas[0].value);
  const [finalQuestions, setFinalQuestions] = useState<Question[]>([]);

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const text = event.target?.result as string;
      const questions = parseQuestionsFromTxt(text);
      if (questions.length > 0) {
        setPendingQuestions(questions);
        setCurrent(0);
        setFinalQuestions([]);
        setSelectedTema(temas[0].value);
      } else {
        alert("No se han detectado preguntas en el archivo.");
      }
    };
    reader.readAsText(file);
  };

  // Parser: detecta opciones y la correcta entre asteriscos
  function parseQuestionsFromTxt(text: string): Omit<Question, "tema">[] {
    const blocks = text.split(/\n\s*\n/);
    return blocks
      .map((block) => {
        const preguntaMatch = block.match(/Pregunta:\s*(.+)/i) || block.match(/^(.+)$/m);
        const opciones: string[] = [];
        let answer = "";
        const optionRegex = /^(\*?)([a-dA-D])\)\s*(.*?)\*?$/gim;
        let match;
        while ((match = optionRegex.exec(block)) !== null) {
          const isCorrect = match[1] === "*" || match[0].trim().endsWith("*");
          const letra = match[2].toUpperCase();
          const texto = match[3].trim().replace(/\*$/, "");
          opciones.push(texto);
          if (isCorrect) answer = letra;
        }
        if (preguntaMatch && opciones.length === 4 && answer) {
          return {
            question: preguntaMatch[1].trim(),
            options: opciones,
            answer,
          };
        }
        return null;
      })
      .filter(Boolean) as Omit<Question, "tema">[];
  }

  const handleTemaSelect = () => {
    const preguntaActual = pendingQuestions[current];
    setFinalQuestions((prev) => [
      ...prev,
      { ...preguntaActual, tema: selectedTema },
    ]);
    if (current + 1 < pendingQuestions.length) {
      setCurrent(current + 1);
      setSelectedTema(temas[0].value);
    } else {
      // Todas asignadas
      onImport([
        ...finalQuestions,
        { ...preguntaActual, tema: selectedTema },
      ]);
      setPendingQuestions([]);
      setCurrent(0);
      setFinalQuestions([]);
    }
  };

  // Si hay preguntas pendientes, muestra el selector de tema
  if (pendingQuestions.length > 0) {
    const pregunta = pendingQuestions[current];
    return (
      <div style={{ background: "#fff", padding: 16, borderRadius: 8, marginBottom: 24 }}>
        <h2>Asigna un tema a la pregunta {current + 1} de {pendingQuestions.length}</h2>
        <div>
          <b>Pregunta:</b> {pregunta.question}
        </div>
        <div>
          <b>Opciones:</b>
          <ul>
            {pregunta.options.map((opt, i) => (
              <li key={i}>
                <b>{["A", "B", "C", "D"][i]}:</b> {opt} {pregunta.answer === ["A", "B", "C", "D"][i] ? "✅" : ""}
              </li>
            ))}
          </ul>
        </div>
        <div>
          <label>
            Tema:
            <select value={selectedTema} onChange={e => setSelectedTema(e.target.value)}>
              {temas.map(t => (
                <option key={t.value} value={t.value}>{t.label}</option>
              ))}
            </select>
          </label>
        </div>
        <button onClick={handleTemaSelect}>Guardar y continuar</button>
      </div>
    );
  }

  // Si no hay preguntas pendientes, muestra el input de archivo
  return (
    <div style={{ margin: "1rem 0" }}>
      <h2>Importar preguntas desde TXT</h2>
      <input type="file" accept=".txt" onChange={handleFileChange} />
      <p style={{ fontSize: "0.9em" }}>
        El archivo debe tener el formato:<br />
        Pregunta: ¿Texto de la pregunta?<br />
        a) Opción A<br />
        *b) Opción B*  ← la respuesta correcta va entre asteriscos<br />
        c) Opción C<br />
        d) Opción D<br />
        (línea en blanco entre preguntas)
      </p>
    </div>
  );
}
